import React, { useState, useEffect } from 'react'
import './App.css'
import axios from "axios"
import { quantile } from './utils/math'
import moment from 'moment'
import {VictoryLine, VictoryChart, VictoryBrushContainer, VictoryTooltip, VictoryZoomContainer} from 'victory'





function App() {
  const [data, setData] = useState({rMSSD: [], SDNN: []});
  const [q25, setQ25] = useState();
  const [q50, setQ50] = useState();
  const [q75, setQ75] = useState();
  const [selectedDomain, setSelectedDomain] = useState();
  const [zoomDomain, setZoomDomain] = useState();


  useEffect(() => {
    axios.get('http://localhost:3001/recent')
      .then((response) => {
        let r = response.data.data

        console.log(r)
        let rMSSDArr = r.map(datum => datum.rMSSD)
        let rMSSDData = r.map(datum => { return {label: datum.rMSSD, y: datum.rMSSD, x: moment(datum.createdAt).toDate()} })
        let SDNNData = r.map(datum => { return {label: datum.SDNN, y: datum.SDNN, x: moment(datum.createdAt).toDate()} })

        setData({rMSSD: rMSSDData, SDNN: SDNNData});
        setQ25(quantile(rMSSDArr, .25))
        setQ50(quantile(rMSSDArr, .50))
        setQ75(quantile(rMSSDArr, .75))
    })
  },[])

  const handleZoom = (domain) => {
    setSelectedDomain(domain)
  }

 const handleBrush = (domain) => {
    setZoomDomain(domain)
  }


      return (
        <div>
            <VictoryChart width={600} height={350} scale={{x: "time"}}
              containerComponent={
                <VictoryZoomContainer responsive={false}
                  zoomDimension="x"
                  zoomDomain={zoomDomain}
                  onZoomDomainChange={handleZoom}
                />
              }

            >
              <VictoryLine
                style={{
                  data: {stroke: "#f7362d"}
                }}
                interpolation="natural"
                data={data.rMSSD}
              />
              <VictoryLine
                style={{
                  data: {stroke: "#20b2aa"}
                }}
                interpolation="natural"
                data={data.SDNN}
              />

            </VictoryChart>

            <VictoryChart
              padding={{top: 0, left: 50, right: 50, bottom: 30}}
              width={600} height={90} scale={{x: "time"}}
              containerComponent={
                <VictoryBrushContainer responsive={false}
                  brushDimension="x"
                  brushDomain={selectedDomain}
                  onBrushDomainChange={handleBrush}
                />
              }
            >

              <VictoryLine
                style={{
                  data: {stroke: "tomato"}
                }}
                interpolation="natural"
                data={data.rMSSD}
              />
            </VictoryChart>
        </div>
      );
  }



export default App;
