import React, { useState, useEffect } from 'react';
import './App.css';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ReferenceLine } from 'recharts';
import axios from "axios";
import { quantile } from './utils/math'
import * as d3 from "d3";
import moment from 'moment'





function App() {
  const [data, setData] = useState();
  const [q25, setQ25] = useState();
  const [q50, setQ50] = useState();
  const [q75, setQ75] = useState();


  useEffect(() => {
    axios.get('http://localhost:3001/recent')
      .then((response) => {
        let r = response.data.data
        let rMSSDArr = r.map(datum => datum.rMSSD)
        r = r.map(datum => { 
          var parseTime = d3.timeParse("%Y-%m-%dT%H:%M:%S.%LZ");
          return {rMSSD: datum.rMSSD, date: parseTime(datum.createdAt)} })
        console.log(r)

        setData(r);
        setQ25(quantile(rMSSDArr, .25))
        setQ50(quantile(rMSSDArr, .50))
        setQ75(quantile(rMSSDArr, .75))
    })
  },[])

  const tickFormatter = (tick) => moment(tick).format('MM/dd')

  return (
    <LineChart width={1000} height={600} data={data}>
      <Line type="natural" dataKey="rMSSD" stroke="#82ca9d" />
      <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
      <ReferenceLine y={q25} label="25th Percentile" stroke="red" strokeDasharray="3 3" />
      <ReferenceLine y={q50} label="50th Percentile" stroke="red" strokeDasharray="3 3" />
      <ReferenceLine y={q75} label="75th Percentile" stroke="red" strokeDasharray="3 3" />
      <XAxis dataKey="date" tickFormatter={tickFormatter} type='number' scale='time' domain={[data[0], data[data.length-1]]}/>
      <YAxis />
      <Tooltip/>
    </LineChart>
  )
}

export default App;
