import React, { useState, useEffect } from 'react'
import './App.css'
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ReferenceLine } from 'recharts'
import axios from "axios"
import { quantile } from './utils/math'
import moment from 'moment'
import { ResponsiveLine } from '@nivo/line'





function App() {
  const [data, setData] = useState();
  const [q25, setQ25] = useState();
  const [q50, setQ50] = useState();
  const [q75, setQ75] = useState();


  useEffect(() => {
    axios.get('http://localhost:3001/recent')
      .then((response) => {
        let r = response.data.data
        let rMSSDArr = r.map(datum => datum.rMSSD)
        let rMSSDData = r.map(datum => { return {y: datum.rMSSD, x: moment(datum.createdAt).toDate()} })
        let d = []
        d = d.append({id: "rMSSD", data: rMSSDData})

        console.log(d)

        setData(d);
        setQ25(quantile(rMSSDArr, .25))
        setQ50(quantile(rMSSDArr, .50))
        setQ75(quantile(rMSSDArr, .75))
    })
  },[])

  const tickFormatter = (tick) => moment(tick).format('MM/dd')

  return (
    // <LineChart width={1000} height={600} data={data}>
    //   <Line type="natural" dataKey="rMSSD" stroke="#82ca9d" />
    //   <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
    //   <ReferenceLine y={q25} label="25th Percentile" stroke="red" strokeDasharray="3 3" />
    //   <ReferenceLine y={q50} label="50th Percentile" stroke="red" strokeDasharray="3 3" />
    //   <ReferenceLine y={q75} label="75th Percentile" stroke="red" strokeDasharray="3 3" />
    //   <XAxis dataKey="date" tickFormatter={tickFormatter}  scale="time" type="number" domain={['dataMin', 'dataMax']}/>
    //   <YAxis />
    //   <Tooltip/>
    // </LineChart>
    <ResponsiveLine
    data={data}
    margin={{ top: 50, right: 110, bottom: 50, left: 60 }}

    xScale={{
      type: 'time',
      format: '%Y-%m-%d',
      precision: 'day',
  }}
    yScale={{ type: 'linear', min: 'auto', max: 'auto', stacked: true, reverse: false }}
    axisTop={null}
    axisRight={null}
    axisBottom={{
        orient: 'bottom',
        tickSize: 5,
        tickPadding: 5,
        tickRotation: 0,
        legend: 'transportation',
        legendOffset: 36,
        legendPosition: 'middle'
    }}
    axisLeft={{
        orient: 'left',
        tickSize: 5,
        tickPadding: 5,
        tickRotation: 0,
        legend: 'count',
        legendOffset: -40,
        legendPosition: 'middle'
    }}
    colors={{ scheme: 'nivo' }}
    pointSize={10}
    pointColor={{ theme: 'background' }}
    pointBorderWidth={2}
    pointBorderColor={{ from: 'serieColor' }}
    pointLabel="y"
    pointLabelYOffset={-12}
    useMesh={true}
    legends={[
        {
            anchor: 'bottom-right',
            direction: 'column',
            justify: false,
            translateX: 100,
            translateY: 0,
            itemsSpacing: 0,
            itemDirection: 'left-to-right',
            itemWidth: 80,
            itemHeight: 20,
            itemOpacity: 0.75,
            symbolSize: 12,
            symbolShape: 'circle',
            symbolBorderColor: 'rgba(0, 0, 0, .5)',
            effects: [
                {
                    on: 'hover',
                    style: {
                        itemBackground: 'rgba(0, 0, 0, .03)',
                        itemOpacity: 1
                    }
                }
            ]
        }
    ]}
/>
  )
}

export default App;
