import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ReferenceLine } from 'recharts';
import axios from "axios";
import {asc, sum, mean, std, quantile} from './utils/math'

//const data = [{name: 'Page A', uv: 400, pv: 2400, amt: 2400}];


function App() {
  const [data, setData] = useState();

  const [q25, setQ25] = useState();
  const [q50, setQ50] = useState();
  const [q75, setQ75] = useState();


  useEffect(() => {
    axios.get('http://localhost:3001/recent')
      .then((response) => {
        let r = response.data.data
        setData(r);
        setQ25(quantile(r.map(datum => datum.rMSSD), .25))
        setQ50(quantile(r.map(datum => datum.rMSSD), .50))
        setQ75(quantile(r.map(datum => datum.rMSSD), .75))
        console.log(response)
    })
  },[])


// const asc = arr => arr.sort((a, b) => a - b);

// const sum = arr => arr.reduce((a, b) => a + b, 0);

// const mean = arr => sum(arr) / arr.length;

// // sample standard deviation
