//     const mu = mean(arr);
//     const diffArr = arr.map(a => (a - mu) ** 2);
//     return Math.sqrt(sum(diffArr) / (arr.length - 1));
// };

// const quantile = (arr, q) => {
//     const sorted = asc(arr);
//     const pos = (sorted.length - 1) * q;
//     const base = Math.floor(pos);
//     const rest = pos - base;
//     if (sorted[base + 1] !== undefined) {
//         return sorted[base] + rest * (sorted[base + 1] - sorted[base]);
//     } else {
//         return sorted[base];
//     }
// };

  return (
  <LineChart width={1000} height={600} data={data}>

    <Line type="monotone" dataKey="rMSSD" stroke="#82ca9d" />
    <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
    <ReferenceLine y={q25} label="25th Quantile" stroke="red" strokeDasharray="3 3" />
    <ReferenceLine y={q50} label="50th Quantile" stroke="red" strokeDasharray="3 3" />
    <ReferenceLine y={q75} label="75th Quantile" stroke="red" strokeDasharray="3 3" />
    <XAxis dataKey="createdAt" />
    <YAxis />
    <Tooltip/>
  </LineChart>
  )}

export default App;
