import React, { useState, useEffect } from 'react'
import './App.css'
import axios from "axios"
import { quantile } from './utils/math'
import moment from 'moment'
import {VictoryLine, VictoryChart, VictoryBrushContainer, VictoryTooltip, createContainer, VictoryLegend} from 'victory'

const VictoryZoomVoronoiContainer = createContainer("zoom", "voronoi");



function App() {
  const [data, setData] = useState({rMSSD: [{x: 1, y:2}], SDNN: []});
  const [q33, setQ33] = useState();
  const [q66, setQ66] = useState();
  const [selectedDomain, setSelectedDomain] = useState();
  const [zoomDomain, setZoomDomain] = useState();


  useEffect(() => {
    axios.get('http://localhost:3001/recent')
      .then((response) => {
        let r = response.data.data

        console.log(r)
        let rMSSDArr = r.map(datum => datum.rMSSD)
        let rMSSDData = r.map(datum => { return {label: datum.rMSSD, y: datum.rMSSD, x: moment(datum.createdAt).toDate()} })
        let SDNNData = r.map(datum => { return {label: datum.SDNN, y: datum.SDNN, x: moment(datum.createdAt).toDate()} })

        setData({rMSSD: rMSSDData, SDNN: SDNNData});
        setQ33(quantile(rMSSDArr, .33))
        setQ66(quantile(rMSSDArr, .66))
    })
  },[])

  const handleZoom = (domain) => {
    setSelectedDomain(domain)
  }

 const handleBrush = (domain) => {
    setZoomDomain(domain)
  }


      return (
        <div>
            <VictoryChart width={1000} height={700} scale={{x: "time"}}
              containerComponent={
                <VictoryZoomVoronoiContainer responsive={false}
                  zoomDimension="x"
                  zoomDomain={zoomDomain}
                  onZoomDomainChange={handleZoom}
                />
              }
            >
              <VictoryLegend x={400} y={50}
                title="HRV Time Domain"
                centerTitle
                orientation="horizontal"
                gutter={20}
                style={{ border: { stroke: "black" }, title: {fontSize: 20 } }}
                data={[
                  { name: "rMSSD", symbol: { fill: "#f7362d"} },
                  { name: "SDNN", symbol: { fill: "#20b2aa" } },
                  { name: "67th / 33rd Percentile", symbol: { fill: "ff665e" } }
                ]}
              />
              <VictoryLine
                style={{
                  data: {stroke: "#f7362d"}
                }}
                interpolation="monotoneX"
                data={data.rMSSD}
                labelComponent={<VictoryTooltip activateData={true}/>}
              />
              <VictoryLine
                style={{
                  data: {stroke: "#20b2aa"}
                }}
                interpolation="monotoneX"
                data={data.SDNN}
                labelComponent={<VictoryTooltip/>}
              />
              <VictoryLine
                style={{
                  data: {stroke: "#ff665e",
                        strokeWidth: .5}
                }}
                data={[{x: data.rMSSD[0].x, y:q66}, {x: data.rMSSD[data.rMSSD.length - 1].x, y:q66}]}
              />
              <VictoryLine
                style={{
                  data: {stroke: "#ff665e",
                        strokeWidth: .5}
                }}
                data={[{x: data.rMSSD[0].x, y:q33}, {x: data.rMSSD[data.rMSSD.length - 1].x, y:q33}]}
              />

            </VictoryChart>

            <VictoryChart
              padding={{top: 0, left: 50, right: 50, bottom: 30}}
              width={1000} height={100} scale={{x: "time"}}
              containerComponent={
                <VictoryBrushContainer responsive={false}
                  brushDimension="x"
                  brushDomain={selectedDomain}
                  onBrushDomainChange={handleBrush}
                />
              }
            >

              <VictoryLine
                style={{
                  data: {stroke: "#493924"}
                }}
                data={data.rMSSD}
                interpolation="monotoneX"
                labelComponent={<VictoryTooltip/>}
              />
            </VictoryChart>
        </div>
      );
  }



export default App;
