import React, { useState, useEffect } from 'react'
import './App.css'
//import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ReferenceLine } from 'recharts'
import axios from "axios"
import { quantile } from './utils/math'
import moment from 'moment'
import { Line } from '@nivo/line'


const commonProperties = {
  width: 900,
  height: 400,
  margin: { top: 20, right: 20, bottom: 60, left: 80 },
  animate: true,
  enableSlices: 'x',
}

const CustomSymbol = ({ size, color, borderWidth, borderColor }) => (
  <g>
      <circle fill="#fff" r={size / 2} strokeWidth={borderWidth} stroke={borderColor} />
      <circle
          r={size / 5}
          strokeWidth={borderWidth}
          stroke={borderColor}
          fill={color}
          fillOpacity={0.35}
      />
  </g>
)


function App() {
  const [data, setData] = useState();
  const [q25, setQ25] = useState();
  const [q50, setQ50] = useState();
  const [q75, setQ75] = useState();


  useEffect(() => {
    axios.get('http://localhost:3001/recent')
      .then((response) => {
        let r = response.data.data
        let rMSSDArr = r.map(datum => datum.rMSSD)
        let rMSSDData = r.map(datum => { return {y: datum.rMSSD, x: moment(datum.createdAt).format("YYYY-MM-DD")} })
        let d = [{id: "rMSSD", data: rMSSDData}]

        console.log(d)

        setData(d);
        setQ25(quantile(rMSSDArr, .25))
        setQ50(quantile(rMSSDArr, .50))
        setQ75(quantile(rMSSDArr, .75))
    })
  },[])

  //const tickFormatter = (tick) => moment(tick).format('MM/dd')

  return (
    // <LineChart width={1000} height={600} data={data}>
    //   <Line type="natural" dataKey="rMSSD" stroke="#82ca9d" />
    //   <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
    //   <ReferenceLine y={q25} label="25th Percentile" stroke="red" strokeDasharray="3 3" />
    //   <ReferenceLine y={q50} label="50th Percentile" stroke="red" strokeDasharray="3 3" />
    //   <ReferenceLine y={q75} label="75th Percentile" stroke="red" strokeDasharray="3 3" />
    //   <XAxis dataKey="date" tickFormatter={tickFormatter}  scale="time" type="number" domain={['dataMin', 'dataMax']}/>
    //   <YAxis />
    //   <Tooltip/>
    // </LineChart>

  <div className="chartcontainer">
    <Line
        {...commonProperties}
        // data={[
        //     {
        //         id: 'fake corp. A',
        //         data: [
        //             { x: '2018-01-01', y: 7 },
        //             { x: '2018-01-02', y: 5 },
        //             { x: '2018-01-03', y: 11 },
        //             { x: '2018-01-04', y: 9 },
        //             { x: '2018-01-05', y: 12 },
        //             { x: '2018-01-06', y: 16 },
        //             { x: '2018-01-07', y: 13 },
        //             { x: '2018-01-08', y: 13 },
        //         ],
        //     },
        //     {
        //         id: 'fake corp. B',
        //         data: [
        //             { x: '2018-01-04', y: 14 },
        //             { x: '2018-01-05', y: 14 },
        //             { x: '2018-01-06', y: 15 },
        //             { x: '2018-01-07', y: 11 },
        //             { x: '2018-01-08', y: 10 },
        //             { x: '2018-01-09', y: 12 },
        //             { x: '2018-01-10', y: 9 },
        //             { x: '2018-01-11', y: 7 },
        //         ],
        //     },
        // ]}
        xScale={{
            type: 'time',
            format: '%Y-%m-%d',
            precision: 'day',
        }}
        xFormat="time:%Y-%m-%d"
        yScale={{
            type: 'linear',
            stacked: false,
        }}
        axisLeft={{
            legend: 'linear scale',
            legendOffset: 12,
        }}
        axisBottom={{
            format: '%b %d',
            tickValues: 'every 2 days',
            legend: 'time scale',
            legendOffset: -12,
        }}
        curve='monotoneX'
        enablePointLabel={true}
        pointSymbol={CustomSymbol}
        pointSize={16}
        pointBorderWidth={1}
        pointBorderColor={{
            from: 'color',
            modifiers: [['darker', 0.3]],
        }}
        useMesh={true}
        enableSlices={false}
    />
  </div>
  )
}

export default App;
