import React, { useState, useEffect } from 'react'
import './App.css'
import axios from "axios"
import { quantile } from './utils/math'
import moment from 'moment'
import {VictoryLine, VictoryChart, VictoryTheme, VictoryZoomContainer} from 'victory'





function App() {
  const [data, setData] = useState({rMSSD: [], SDNN: []});
  const [q25, setQ25] = useState();
  const [q50, setQ50] = useState();
  const [q75, setQ75] = useState();


  useEffect(() => {
    axios.get('http://localhost:3001/recent')
      .then((response) => {
        let r = response.data.data
        console.log(r)
        let rMSSDArr = r.map(datum => datum.rMSSD)
        let rMSSDData = r.map(datum => { return {y: datum.rMSSD, x: moment(datum.createdAt).toDate()} })
        let SDNNData = r.map(datum => { return {y: datum.SDNN, x: moment(datum.createdAt).toDate()} })

        setData({rMSSD: rMSSDData, SDNN: SDNNData});
        setQ25(quantile(rMSSDArr, .25))
        setQ50(quantile(rMSSDArr, .50))
        setQ75(quantile(rMSSDArr, .75))
    })
  },[])



  return (
    <VictoryChart
    theme={VictoryTheme.material}
    containerComponent={<VictoryZoomContainer zoomDomain={{x: [5, 35], y: [0, 100]}}/>}
    animate={{
      duration: 2000,
      onLoad: { duration: 1000 }
    }}
    >
    <VictoryLine
      style={{
        data: { stroke: "#c43a31" },
        parent: { border: "1px solid #ccc"}
      }}
      data={data.rMSSD}

    />
    <VictoryLine
      style={{
        data: { stroke: "yellow" },
        parent: { border: "1px solid #ccc"}
      }}
      data={data.SDNN}

    />
  </VictoryChart>
  )
}

export default App;
