import React, { useState, useEffect } from 'react'
import './App.css'
import axios from "axios"
import { quantile } from './utils/math'
import moment from 'moment'
import {VictoryLine, VictoryChart} from 'victory'





function App() {
  const [data, setData] = useState([{id: 'fake corp. A', data: [{x: '1985-21-05', y: 1}]}]);
  const [q25, setQ25] = useState();
  const [q50, setQ50] = useState();
  const [q75, setQ75] = useState();


  useEffect(() => {
    axios.get('http://localhost:3001/recent')
      .then((response) => {
        let r = response.data.data
        console.log(r)
        let rMSSDArr = r.map(datum => datum.rMSSD)
        let rMSSDData = r.map(datum => { return {y: datum.rMSSD, x: moment(datum.createdAt).format("YYYY-MM-DD")} })
        let SDNNData = r.map(datum => { return {y: datum.SDNN, x: moment(datum.createdAt).format("YYYY-MM-DD")} })
        let d = [{id: "rMSSD", data: rMSSDData}, {id:"SDNN", data: SDNNData}]

        console.log(d)

        setData(d);
        setQ25(quantile(rMSSDArr, .25))
        setQ50(quantile(rMSSDArr, .50))
        setQ75(quantile(rMSSDArr, .75))
    })
  },[])



  return (
    <VictoryChart
    theme={VictoryTheme.material}
  >
    <VictoryLine
      style={{
        data: { stroke: "#c43a31" },
        parent: { border: "1px solid #ccc"}
      }}
      data={[
        { x: 1, y: 2 },
        { x: 2, y: 3 },
        { x: 3, y: 5 },
        { x: 4, y: 4 },
        { x: 5, y: 7 }
      ]}
    />
  </VictoryChart>
  )
}

export default App;
